
public class ParseInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String value = "6";//Declarando uma string
		int x = Integer.parseInt(value); // Fazendo o parse, ou seja, o convert de string para int, no parametro se passa uma STRING
		System.out.println(x); // Printando o valor como int
	}

}
